var parser = require("neo4j-parser");

// Example
// Parse the results of 2 queries, the first one should be parsed raw (unparsed)

var response = {
  "results": [
    {
      "columns": [
        "value"
      ],
      "data": [
        {
          "row": [
            "hello"
          ],
        }
      ],
  },{
    "columns": [
      "value2"
    ],
    "data": [
      {
        "row": [
          "hello2"
        ],
      }
    ],
  }
  ],
  "errors": []
};

parser({
  body: JSON.stringify(response),
  cb: function(err, index, resultObj)
  {
    console.log(JSON.stringify(arguments));
  },
  raw: false,
  isRaw: function(index)
  {
      if(index === 0) return true;
      else return false;
  }
});
